package com.measure;import android.app.Activity;import android.content.Intent;import android.hardware.SensorEvent;import android.hardware.SensorManager;import android.os.Bundle;import android.view.View;import android.view.View.OnClickListener;import android.view.Window;import android.view.WindowManager;import android.widget.Button;import android.widget.PopupWindow;import android.widget.TextView;public class AngleSelect extends Activity {	private Button bt1, bt2, bt3;	public void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		requestWindowFeature(Window.FEATURE_NO_TITLE);		this.getWindow().setFlags(WindowManager.LayoutParams.FLAG_FULLSCREEN,				WindowManager.LayoutParams.FLAG_FULLSCREEN);		setContentView(R.layout.angle_select);		bt1 = (Button) findViewById(R.id.button1);		bt2 = (Button) findViewById(R.id.button2);		bt3 = (Button) findViewById(R.id.button3);		bt1.setOnClickListener(new OnClickListener() {			@Override			public void onClick(View v) {				Intent intent = new Intent();				intent.setClass(AngleSelect.this, Angle.class);				intent.putExtra("type", 0);				AngleSelect.this.startActivity(intent);			}		});		bt2.setOnClickListener(new OnClickListener() {			@Override			public void onClick(View v) {				Intent intent = new Intent();				intent.setClass(AngleSelect.this, Angle.class);				intent.putExtra("type", 1);				AngleSelect.this.startActivity(intent);			}		});		bt3.setOnClickListener(new OnClickListener() {			@Override			public void onClick(View v) {				Intent intent = new Intent();				intent.setClass(AngleSelect.this, Angle.class);				intent.putExtra("type", 2);				AngleSelect.this.startActivity(intent);			}		});	}}