package com.measure;import android.hardware.Sensor;import android.hardware.SensorEvent;import android.hardware.SensorEventListener;import android.hardware.SensorManager;import android.os.Bundle;import android.app.Activity;import android.content.Intent;import android.view.Menu;import android.view.View;import android.view.View.OnClickListener;import android.view.Window;import android.widget.Button;import android.widget.TextView;public class Measure extends Activity {	Button bt_length, bt_angle,bt_radiation,bt_gradienter;	public void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		requestWindowFeature(Window.FEATURE_NO_TITLE);		setContentView(R.layout.main_colorful);				bt_length = (Button) findViewById(R.id.bt_length);		bt_angle = (Button) findViewById(R.id.bt_angle);		bt_radiation = (Button) findViewById(R.id.bt_radiation);		bt_gradienter = (Button) findViewById(R.id.bt_gradienter);				bt_length.setOnClickListener(new OnClickListener() {			@Override			public void onClick(View v) {				Intent intent = new Intent();				intent.setClass(Measure.this, Length.class);				Measure.this.startActivity(intent);			}		});		bt_angle.setOnClickListener(new OnClickListener() {			@Override			public void onClick(View v) {				Intent intent = new Intent();				intent.setClass(Measure.this, Angle.class);				Measure.this.startActivity(intent);			}		});				bt_radiation.setOnClickListener(new OnClickListener() {			@Override			public void onClick(View v) {				Intent intent = new Intent();				intent.setClass(Measure.this, Radiation.class);				Measure.this.startActivity(intent);			}		});				bt_gradienter.setOnClickListener(new OnClickListener() {			@Override			public void onClick(View v) {				Intent intent = new Intent();				intent.setClass(Measure.this, Gradienter.class);				Measure.this.startActivity(intent);			}		});	}}