package com.z;import com.measure.R;import com.measure.R.id;import com.measure.R.layout;import com.measure.util.GetLocationData;import android.app.Activity;import android.os.Bundle;import android.util.Log;import android.view.View;import android.view.View.OnClickListener;import android.widget.Button;public class Distance extends Activity {	Button button;	GetLocationData location;	public void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.distance);				button =(Button)findViewById(R.id.distance_button);		location=new GetLocationData(this.getApplication());				button.setOnClickListener(new OnClickListener() {						@Override			public void onClick(View v) {				if(location.getLatitude()==4.9E-324){					button.setText("damn");				}				else{				button.setText("loction:"+location.getLatitude()+"  "+location.getLongitude());				}			}		});			}}